	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//La tabla del multiplicar del 5, pero empezando 
		//en el 10 (5x10, 5x9, ...)
		int num=5;
		for (int i = 10; i >=1; i--) {
			System.out.println("5 x "+ i +" = "+i*num);
		}
		
	}

